import { ViewportScroller } from '@angular/common';
import { Component } from '@angular/core';
import { ActivatedRoute, NavigationEnd, Router } from '@angular/router';
import { environment } from 'src/environments/environment';
import '@angular/localize/init';

interface ILangOpt {
  id: string;
  label: string;
  icon: string;
  url: string;
}

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
})
export class HeaderComponent {
  languages: ILangOpt[] = [];
  franceLink: string = environment.defaultURL;
  englishLink: string = environment.englishURL;

  constructor(
    private viewportScroller: ViewportScroller,
    private router: Router
  ) {
    router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        const currentUrl = router.url.replace('/', '');
        let newUrl = '';
        this.languages.forEach((lang) => {
          if (lang.id === 'fr') {
            newUrl = `${environment.defaultURL}${currentUrl}`;
          } else {
            newUrl = `${environment.englishURL}${currentUrl}`;
          }
          lang.url = newUrl;
        });
      }
    });
  }

  ngOnInit() {
    this.languages = [
      {
        id: 'fr',
        label: $localize`:@@language_fr:Fran√ßais`,
        icon: 'data:image/png;base64,',
        url: this.franceLink,
      },
      {
        id: 'en',
        label: $localize`:@@language_en:English`,
        icon: 'data:image/png;base64,',
        url: this.englishLink,
      },
    ];
  }

  scrollToContent(elementId: string) {
    this.viewportScroller.scrollToAnchor(elementId);
  }
}
